import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './Form.css';

const initialValue = {
  name: '',
  imageUrl: '',

}

const Cadastro = ({ id }) => {
  const [values, setValues] = useState(id ? null: initialValue);
  const history = useHistory();

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:3001/shoes/${id}`)
        .then((response) => {
          setValues(response.data);
        })
    }
  }, []);

  function onChange(ev) {
    const { name, value } = ev.target;

    setValues({ ...values, [name]: value });
  }

  function onSubmit(ev) {
    ev.preventDefault();

    const method = id ? 'put' : 'post';
    const url = id
      ? `http://localhost:5000/promotions/${id}`
      : 'http://localhost:5000/promotions'

    axios[method](url, values)
      .then((response) => {
        history.push('/');
      });
  }
  return (
    <div>
      <Header />
      <div className="bloco-form">
        <h2>
          {editing
            ? `Editando: ${shoes.find((s) => s._id === editingId)?.name}`
            : "Cadastre um novo Calçado"}
        </h2>

        <form onSubmit={onSubmit}>
          <input
            placeholder="Nome"
            value={nameShoes}
            onChange={(event) => setNameShoes(event.target.value)}
          />
          <br />
          <input
            placeholder="Url da Imagem"
            value={imageUrlShoes}
            onChange={(event) => setImageUrlShoes(event.target.value)}
          />
          <br />
          <button type="submit">Salvar</button>
        </form>
      </div>
    </div>
  );
}
