import React from 'react';
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import BorderColorIcon from '@material-ui/icons/BorderColor';
import Header from '../Header';
import { Link } from "react-router-dom";
import './Main.css';

export default function Main (){
  const [calcados,setCalcados] = useState([
    {
      id:1,
      nome:"Tênis Nike Zoom Freak 3 Unissex",
      imagemUrl: NikeZoom,
      modelo:'Unissex',
      preco:799.99
    },
    {
      id:2,
      nome:"Tênis Nike Air Max Sphere Masculino",
      imagemUrl: NikeAirMax,
      modelo:'Masculino',
      preco:999.99
    },
    {
      id:3,
      nome:"Tênis Nike Max 90 SE Infantil",
      imagemUrl: NikeMax,
      modelo:'Infantil',
      preco:299.99
    },
    {
      id:4,
      nome:"Tênis Nike Space Hippie 04 Feminino",
      imagemUrl: NikeSpace,
      modelo:'Feminino',
      preco:799.99
    },
    { 
      id:5,
      nome:"Tênis Nike Flex Runner Infantil",
      imagemUrl: NikeRunner,
      modelo:'Infantil',
      preco: 299.99
    },
    { 
      id:6,
      nome:"Tênis Nike Metcon 7 Feminino",
      imagemUrl: NikeMetcon,
      modelo:'Feminino',
      preco:749.99
    },
    { 
      id:7,
      nome:"Tênis NikeCourt React Vapor NXT Masculino",
      imagemUrl: NikeCourt,
      modelo:'Masculino',
      preco:"1.999.99"
    },
    { 
      id:8,
      nome:"Tênis Nike Pico 5 Infantil",
      imagemUrl: NikePico,
      modelo:'Infantil',
      preco:179.99
    }  
  ])

  return(

    <div className="container">
      <Header />  
      <main className="main">
        <ul>
          {calcados.map((calcado,index)=>(
            <li key = {index}>
              <div className="imagem">
                <img src={calcado.imagemUrl} alt={calcado.nome}/>
                <div className='botoes'>
                <Button variant="contained" color="primary">
                  Comprar
                </Button>
                <div className="acoes">
                  <Link to="/cadastro" className="btn"> <button className='botaoIcon'><BorderColorIcon className='icon' id='edit' /></button> </Link>
                  <button className='botaoIcon' ><DeleteIcon className='icon' id='delete' /></button>
                </div>
                </div>
              </div>
              <div className='info'>
              <h3>{calcado.nome}</h3>
              <span>R${calcado.preco}</span>
              </div>
            </li>


          ))}
        </ul>
      </main>
    </div>
  );
}
