import React, { useEffect, useState } from "react";
import axios from 'axios';
import DeleteIcon from "@material-ui/icons/Delete";
import Button from "@material-ui/core/Button";
import BorderColorIcon from "@material-ui/icons/BorderColor";
import Header from "../Header";
import "./Main.css";
import { Link } from "react-router-dom";

const BASE_URL = "http://localhost:3001";

export default function Main() {
  const [shoes, setShoes] = useState([]);

  const loadShoes = () => {
    axios.get(`${BASE_URL}/shoes`)
    .then((response)=>{
      console.log(response.data)
    })
    // setShoes(data);

  };

  useEffect(() => {
    loadShoes();
  }, []);

  const exclude = async (id) =>{
    await fetch(`${BASE_URL}/shoes/${id}`,{
      method: "DELETE",
    });
    loadShoes();
  };

  return (
    <div className="container">
      <Header />

      <main className="main">
        <ul>
          {shoes.map((calcado, index) => (
            <li key={index}>
              <div className="imagem">
                <img src={calcado.imageUrl} alt={calcado.name} />
                <div className="botoes">
                  <Button variant="contained" color="primary">
                    Comprar
                  </Button>
                  <div className="acoes">

                    <Link
                      to={`/edit/${calcado._id}`}
                      className="botaoIcon"
                    >
                      <BorderColorIcon className="icon" id="edit" />
                    </Link>
                    <button 
                      className="botaoIcon"
                      onClick={() => exclude(calcado._id)}
                    >
                      <DeleteIcon className="icon" id="delete" />
                    </button>
                  </div>
                </div>
              </div>
              <div className="info">
                <h3>{calcado.name}</h3>
                <span>R${calcado.price}</span>
              </div>
            </li>
          ))}
        </ul>
      </main>
    </div>
  );
}
