import React, { useEffect, useState } from 'react';
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import BorderColorIcon from '@material-ui/icons/BorderColor';
import Header from '../Header';
import { Link } from "react-router-dom";
import './Main.css';

const BASE_URL = "http://localhost:3001"

export default function Main (){
  const [shoes,setShoes] = useState([]);
  const [nameShoes,setNameShoes] = useState("");
  const [imageUrlShoes, setImageUrlShoes] = useState("");
  const [editing,setEditing] = useState(false);
  const [editingIndex,setEditingIndex] = useState(null);

  const loadShoes = async () =>{
    const response = await fetch(`${BASE_URL}/shoes`)
    const data = await response.json();

    setShoes(data);
  }

  useEffect(() => {
    loadShoes();
  },[])

  useEffect(()=>{
    if (editingIndex !== null && editing){
      setNameShoes(shoes[editingIndex].name);
    }
  })

  return(

    <div className="container">
      <Header />  
      <main className="main">
        <ul>
          {shoes.map((calcado,index)=>(
            <li key = {index}>
              <div className="imagem">
                <img src={calcado.imageUrl} alt={calcado.name}/>
                <div className='botoes'>
                <Button variant="contained" color="primary">
                  Comprar
                </Button>
                <div className="acoes">
                  <Link to="/cadastro" className="btn"> <button className='botaoIcon'><BorderColorIcon className='icon' id='edit' /></button> </Link>
                  <button className='botaoIcon' ><DeleteIcon className='icon' id='delete' /></button>
                </div>
                </div>
              </div>
              <div className='info'>
              <h3>{calcado.name}</h3>
              <span>R${calcado.preco}</span>
              </div>
            </li>


          ))}
        </ul>
      </main>
    </div>
  );
}
