import React, { useEffect, useState } from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import Button from "@material-ui/core/Button";
import BorderColorIcon from "@material-ui/icons/BorderColor";
import Header from "../Header";
import { Link } from "react-router-dom";
import "./Main.css";

const BASE_URL = "http://localhost:3001";

export default function Main() {
  const [shoes, setShoes] = useState([]);
  const [nameShoes, setNameShoes] = useState("");
  const [imageUrlShoes, setImageUrlShoes] = useState("");
  const [editing, setEditing] = useState(false);
  const [editingId, setEditingId] = useState(null);

  const loadShoes = async () => {
    const response = await fetch(`${BASE_URL}/shoes`);
    const data = await response.json();

    setShoes(data);
  };

  useEffect(() => {
    loadShoes();
  }, []);

  useEffect(() => {
    if (editingId !== null && editing) {
      const shoe = shoes.find((s) => s._id === editingId);
      setNameShoes(shoe.name);
      setImageUrlShoes(shoe.imageUrl);
    }
  }, [editingId]);

  const onSubmit = async (event)=>{
    event.preventDefault();

    if(editing){

    }
  }

  return (
    <div className="container">
      <Header />
      <div className="bloco-form">
        <h2>
          {editing
            ? `Editando: ${shoes.find((s) => s._id === editingId)?.name}`
            : "Cadastre um novo CalÃ§ado"
          }
        </h2>

        <form onSubmit={onSubmit}>
          <input 
            placeholder="Nome"
            value={nameShoes}
            onChange={(event)=> setNameShoes(event.target.value)}
          />
          <br />
          <input 
            placeholder="Url da Imagem"
            value={imageUrlShoes}
            onChange={(event)=> setImageUrlShoes(event.target.value)} 
          />
          <br />
          <button type="submit">Salvar</button>
        </form>
      </div>
      <main className="main">
        <ul>
          {shoes.map((calcado, index) => (
            <li key={index}>
              <div className="imagem">
                <img src={calcado.imageUrl} alt={calcado.name} />
                <div className="botoes">
                  <Button variant="contained" color="primary">
                    Comprar
                  </Button>
                  <div className="acoes">
                    <button 
                      className="botaoIcon"
                      onClick={()=>{
                        setEditing(true);
                        setEditingId(calcado._id)
                      }}
                    >
                      <BorderColorIcon className="icon" id="edit" />
                    </button>
                    <button className="botaoIcon">
                      <DeleteIcon className="icon" id="delete" />
                    </button>
                  </div>
                </div>
              </div>
              <div className="info">
                <h3>{calcado.name}</h3>
                <span>R${calcado.preco}</span>
              </div>
            </li>
          ))}
        </ul>
      </main>
    </div>
  );
}
