import React from 'react';
// import NikeAir from './img/Nike-Air.png'
import './App.css';

const NikeAir = require('./img/Nike-Air.png')
export default class App extends React.Component{
  constructor(props){
    super(props);
    this.state={

      calcados:[
        {
          id:1,
          nome:"Nike Air",
          imagemUrl: {NikeAir}
        },
        {
          id:2,
          nome:"Nike Air Max",
          imagemUrl:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSg4yMt1h_qRatbFR270lQ7VDszkRY9lVxHMQ&usqp=CAU"
        },
        {
          id:3,
          nome:"Nike Anti-Clog",
          class:"imagem",
          imagemUrl:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQbXAerwmcWqEAUPNP7Gja0UioRQDPVHlqIFQ&usqp=CAU"
        },
        {
          id:4,
          nome:"Nike Flyease",
          imagemUrl:"https://images.lojanike.com.br/500x500/produto/tenis-nike-air-zoom-tempo-next-flyease-masculino-CV1889-102-1.jpg"
        },
        { 
          id:5,
          nome:"Nike Flyknit",
          imagemUrl:"https://images.lojanike.com.br/500x500/produto/tenis-nike-air-vapormax-2020-flyknit-feminino-CT1933-002-1.jpg"
        } 

      ]
    };
  }
  render(){
    const {calcados} = this.state;

    return(
      <div className="container">
        <header className="header">
        <h1>Calçados</h1>
          <ul>
            <li><a href='#'>Lançamentos</a></li>
            <li><a href='#'>Feminino</a></li>
            <li><a href='#'>Masculino</a></li>
            <li><a href='#'>Infantil</a></li>
          </ul>
        </header>
        <main className="main">
          <ul>
            {calcados.map((calcado)=>(
              <li key = {calcado.id}>
                <img src={NikeAir} className={calcado.class} alt={calcado.nome}/>
                <h3>{calcado.nome}</h3>
              </li>
            ))}
          </ul>
        </main>
      </div>
    );
  }
}
