import React,{ useState, useEffect } from "react";
import axios from "axios";
import DeleteIcon from "@material-ui/icons/Delete";
import Button from "@material-ui/core/Button";
import BorderColorIcon from "@material-ui/icons/BorderColor";
import { Link } from "react-router-dom";
import Header from "../Header";
import "./Feminino.css";

const BASE_URL = "http://localhost:3001";

function Feminino() {
  const [shoes, setShoes] = useState([]);

  useEffect(() => {
    axios.get(`${BASE_URL}/shoes`).then((response) => {
      setShoes(response.data);
    });
  }, []);

  return (
    <div>
      <Header />

      <ul>
        {shoes
          .filter(
            (calcado) =>
              calcado.model === "Feminino" || calcado.model === "Unissex"
          )
          .map((calcado, index) => (
            <li key={index}>
              <div className="item">
                <img
                  src={calcado.imageUrl}
                  className={calcado.class}
                  alt={calcado.name}
                />
                <div className="botoes">
                  <Button variant="contained" color="primary">
                    Comprar
                  </Button>
                  <div className="acoes">
                    <Link
                      to={`/edit/${calcado._id}`}
                      className="botaoIcon"
                    >
                      <BorderColorIcon className="icon" id="edit" />
                    </Link>
                    <button className="botaoIcon">
                      <DeleteIcon className="icon" id="delete" />
                    </button>
                  </div>
                </div>
              </div>

              <div className="info">
                <h3>{calcado.name}</h3>
                <span>R${calcado.price}</span>
              </div>
            </li>
          ))}
      </ul>
    </div>
  );
}

export default Feminino;
