import React from 'react';
import Cadastro from '../Cadastro';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import BorderColorIcon from '@material-ui/icons/BorderColor';
import { exportDefaultDeclaration } from '@babel/types';


function Main (){

    return(

        <div className="container">

          <header className="header">
          <h1>Calçados</h1>
          <hr/>
            <ul>
              <li><a href='#'>Lançamentos</a></li>
              <li><a href='#'>Feminino</a></li>
              <li><a href='#'>Masculino</a></li>
              <li><a href='#'>Infantil</a></li>
              <li className='search'>
                <FontAwesomeIcon icon={faSearch} />
                <input type='text' placeholder="O que você procura?"></input>


              </li>
            </ul>
            <Link to="/cadastro" className="btn"><button > Cadastre-se</button></Link>
            <hr/>
          </header>
          <main className="main">

            <ul>
              {Cadastro.map((calcado,index)=>(
                <li key = {index}>
                  <div className="imagem">
                    <img src={calcado.imagemUrl} className={calcado.class} alt={calcado.nome}/>
                    <div className='botoes'>

                    <Button variant="contained" color="primary">
                      Comprar
                    </Button>
                    <div className="acoes">
                      <button onClick={() => {
                        this.setState({
                          editando: true,
                          indexEditando: index,
                          nomeCalcado: calcado.nome,
                          imagemUrlCalcado: calcado.imagemUrl,
                        });
                      }} className='botaoIcon'><BorderColorIcon className='icon' id='edit' /></button>
                      <button onClick={() => this.deletar(index)} className='botaoIcon' ><DeleteIcon className='icon' id='delete' /></button>
                    </div>
                    </div>
                  </div>
                  <div className='info'>

                  <h3>{calcado.nome}</h3>

                  </div>
                </li>


              ))}
            </ul>
          </main>
        </div>
      );
    }



export default Main;
