import React from 'react';
import NikeAir from '../../img/Nike-Air.png'
import NikeAirMax from '../../img/Nike-Air.png'
import NikeAntiClog from '../../img/Nike-Air.png'
import NikeFlyease from '.../../img/Nike-Air.png'
import NikeFlyknit from '../../img/Nike-Air.png'
import NikeKD14 from '../../img/Nike-Air.png'
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import BorderColorIcon from '@material-ui/icons/BorderColor';
export default class App extends React.Component{
    constructor(props){
      super(props);
      this.state={

        calcados:[
          {
            id:1,
            nome:"Nike Air",
            imagemUrl: NikeAir
          },
          {
            id:2,
            nome:"Nike Air Max",
            imagemUrl:NikeAirMax
          },
          {
            id:3,
            nome:"Nike Anti-Clog",
            imagemUrl:NikeAntiClog
          },
          {
            id:4,
            nome:"Nike Flyease",
            imagemUrl:NikeFlyease
          },
          { 
            id:5,
            nome:"Nike Flyknit",
            imagemUrl:NikeFlyknit
          },
          { 
            id:6,
            nome:"Nike KD14",
            imagemUrl:NikeKD14
          }  

        ],
        nomeCalcado: "",
        imagemUrlCalcado: "",
        editando: false,
        indexEditando: null
      };
    }

    onSubmit = (event) =>{
      event.preventDefault();
      const {calcados, nomeCalcado,imagemUrlCalcado,editando,indexEditando} = this.state;

      if(editando){
        const calcadosAtualizados =  calcados.map((calcado,index)=>{
          if(indexEditando === index){
            calcado.nome = nomeCalcado;
            calcado.imagemUrl = imagemUrlCalcado;
          }

          return calcado;
        });

        this.setState({
          calcados: calcadosAtualizados,
          indexEditando: null,
          editando:false
        });

      }else{
        this.setState({
          calcados:[
            ...calcados,
            {
              nome: nomeCalcado,
              imagemUrl:imagemUrlCalcado,
            },
          ],
        });
      }

      this.setState({
        nomeCalcado:'',
        imagemUrlCalcado:''
      });
    };

    deletar = (index) =>{
      const { calcados } = this.state;
      this.setState({
        calcados: calcados.filter((calcado,i) => i !== index )
      });
    };

    render(){
      const {calcados,nomeCalcado,imagemUrlCalcado,editando,indexEditando} = this.state;

      return(

        <div className="container">
          <Routes />
          <header className="header">
          <h1>Calçados</h1>
          <hr/>
            <ul>
              <li><a href='#'>Lançamentos</a></li>
              <li><a href='#'>Feminino</a></li>
              <li><a href='#'>Masculino</a></li>
              <li><a href='#'>Infantil</a></li>
              <li className='search'>
                <FontAwesomeIcon icon={faSearch} />
                <input type='text' placeholder="O que você procura?"></input>


              </li>
            </ul>
            <hr/>
          </header>
          <main className="main">
            <div className='bloco-form'>
              <h2>
              {editando
                ? `Editando: ${calcados[indexEditando]?.nome}`
                : "Cadastre um novo Calçado"}

              </h2>
              <form onSubmit={this.onSubmit}>
              <input
                placeholder="Nome"
                value={nomeCalcado}
                onChange={(event) => {
                  this.setState({
                    nomeCalcado: event.target.value,
                  });
                }}
              />
              <br />
              <input
                placeholder="Url da Imagem"
                value={imagemUrlCalcado}
                onChange={(event) => {
                  this.setState({
                    imagemUrlCalcado: event.target.value,
                  });
                }}
              />
              <br />
              <button type="submit">Salvar</button>
            </form>
            </div>
            <ul>
              {calcados.map((calcado,index)=>(
                <li key = {index}>
                  <div className="imagem">
                    <img src={calcado.imagemUrl} className={calcado.class} alt={calcado.nome}/>
                    <div className='botoes'>

                    <Button variant="contained" color="primary">
                      Comprar
                    </Button>
                    <div className="acoes">
                      <button onClick={() => {
                        this.setState({
                          editando: true,
                          indexEditando: index,
                          nomeCalcado: calcado.nome,
                          imagemUrlCalcado: calcado.imagemUrl,
                        });
                      }} className='botaoIcon'><BorderColorIcon className='icon' id='edit' /></button>
                      <button onClick={() => this.deletar(index)} className='botaoIcon' ><DeleteIcon className='icon' id='delete' /></button>
                    </div>
                    </div>
                  </div>
                  <div className='info'>

                  <h3>{calcado.nome}</h3>

                  </div>
                </li>


              ))}
            </ul>
          </main>
        </div>
      );
    }
  }
